template.value:
//---------------------------------------------------------------------------------------------------------
// 												INCLUDES
//---------------------------------------------------------------------------------------------------------

#include <$CLASS$.h>
#include <$MAPTYPE$AnimatedSprite.h>


//---------------------------------------------------------------------------------------------------------
// 												DECLARATIONS
//---------------------------------------------------------------------------------------------------------

extern BYTE $IMG$Tiles[];
extern BYTE $IMG$Map[];


//---------------------------------------------------------------------------------------------------------
//											ANIMATION DEFINITION
//---------------------------------------------------------------------------------------------------------

AnimationFunctionROMDef $NAME$_DEFAULT_ANIM =
{
	// number of frames of this animation function
	2,

	// frames to play in animation
	{0, 1},

	// number of cycles a frame of animation is displayed
	8,

	// whether to play it in loop or not
	true,

	// method to call on function completion
	NULL,

	// function's name
	"Default",
};

AnimationDescriptionROMDef $NAME$_ANIM =
{
	// animation functions
	{
		(AnimationFunction*)&$NAME$_DEFAULT_ANIM,
		NULL,
	}
};


//---------------------------------------------------------------------------------------------------------
// 											SPRITE DEFINITION
//---------------------------------------------------------------------------------------------------------

CharSetROMDef $NAME$_CH =
{
	// number of chars, depending on allocation type:
	// __ANIMATED_SINGLE*, __ANIMATED_SHARED*: number of chars of a single animation frame (cols * rows)
	// __ANIMATED_MULTI, __NOT_ANIMATED: sum of all chars
    $NUMCHARS$,

	// allocation type
	// (__ANIMATED_SINGLE, __ANIMATED_SINGLE_OPTIMIZED, __ANIMATED_SHARED, __ANIMATED_SHARED_COORDINATED, __ANIMATED_MULTI or __NOT_ANIMATED)
    $ATYPE$,

    // char definition
    $IMG$Tiles,
};

TextureROMDef $NAME$_TX =
{
    // charset definition
    (CharSetDefinition*)&$NAME$_CH,

    // bgmap definition
    $IMG$Map,

    // cols (max 64)
    $FRAMECOLS$,

    // rows (max 64)
    $FRAMEROWS$,

	// padding for affine/hbias transformations (cols, rows)
	{$PADDINGX$, $PADDINGY$},

	// number of frames, depending on charset's allocation type:
	// __ANIMATED_SINGLE*, __ANIMATED_SHARED*, __NOT_ANIMATED: 1
	// __ANIMATED_MULTI: total number of frames
    $FRAMENUM$,

    // palette number (0-3)
    $PALETTE$,

	// recyclable
	$RECYCLEABLE$,
};

$MAPTYPE$SpriteROMDef $NAME$_SPRITE =
{
	{
		// sprite's type
		__TYPE($MAPTYPE$AnimatedSprite),

		// texture definition
		(TextureDefinition*)&$NAME$_TX,

		// transparent (__TRANSPARENCY_NONE, __TRANSPARENCY_EVEN or __TRANSPARENCY_ODD)
		$TRANSPARENCY$,

		// displacement
		{$DISPX$, $DISPY$, $DISPZ$, $DISPP$},
	},

	// bgmap mode (__WORLD_BGMAP, __WORLD_AFFINE, __WORLD_OBJECT or __WORLD_HBIAS)
	// make sure to use the proper corresponding sprite type throughout the definition (BgmapSprite or ObjectSprite)
	__WORLD_$MODE$,

	// pointer to affine/hbias manipulation function
	$AFFINEFUNCTION$,

	// display mode (__WORLD_ON, __WORLD_LON or __WORLD_RON)
	__WORLD_$DISPMODE$,
};

$MAPTYPE$SpriteROMDef* const $NAME$_SPRITES[] =
{
	&$NAME$_SPRITE,
	NULL
};


//---------------------------------------------------------------------------------------------------------
// 											ENTITY DEFINITION
//---------------------------------------------------------------------------------------------------------

$CLASS$ROMDef $NAME$_AE =
{
	{
		// class allocator
		__TYPE($CLASS$),

		// sprites
		(SpriteROMDef**)$NAME$_SPRITES,

		// collision shapes
		(ShapeDefinition*)NULL,

		// size
		// if 0, width and height will be inferred from the first sprite's texture's size
		{$WIDTH$, $HEIGHT$, $DEPTH$},

		// gameworld's character's type
		$IGTYPE$,

		// physical specification
		(PhysicalSpecification*)NULL,
	},

	// pointer to the animation definition for the item
	(AnimationDescription*)&$NAME$_ANIM,

	// initial animation
	"Default",
};

//---------------------------------------------------------------------------------------------------------
// 												INCLUDES
//---------------------------------------------------------------------------------------------------------

#include <$CLASS$.h>
#include <$MAPTYPE$Sprite.h>


//---------------------------------------------------------------------------------------------------------
// 												DECLARATIONS
//---------------------------------------------------------------------------------------------------------

extern BYTE $IMG$Tiles[];
extern BYTE $IMG$Map[];


//---------------------------------------------------------------------------------------------------------
// 											SPRITE DEFINITION
//---------------------------------------------------------------------------------------------------------

CharSetROMDef $NAME$_CH =
{
	// number of chars, depending on allocation type:
	// __ANIMATED_SINGLE*, __ANIMATED_SHARED*: number of chars of a single animation frame (cols * rows)
	// __ANIMATED_MULTI, __NOT_ANIMATED: sum of all chars
    $NUMCHARS$,

	// allocation type
	// (__ANIMATED_SINGLE, __ANIMATED_SINGLE_OPTIMIZED, __ANIMATED_SHARED, __ANIMATED_SHARED_COORDINATED, __ANIMATED_MULTI or __NOT_ANIMATED)
    $ATYPE$,

    // char definition
    $IMG$Tiles,
};

TextureROMDef $NAME$_TX =
{
    // charset definition
    (CharSetDefinition*)&$NAME$_CH,

    // bgmap definition
    $IMG$Map,

    // cols (max 64)
    $FRAMECOLS$,

    // rows (max 64)
    $FRAMEROWS$,

	// padding for affine/hbias transformations (cols, rows)
	{$PADDINGX$, $PADDINGY$},

	// number of frames, depending on charset's allocation type:
	// __ANIMATED_SINGLE*, __ANIMATED_SHARED*, __NOT_ANIMATED: 1
	// __ANIMATED_MULTI: total number of frames
    $FRAMENUM$,

    // palette number (0-3)
    $PALETTE$,

	// recyclable
	$RECYCLEABLE$,
};

$MAPTYPE$SpriteROMDef $NAME$_SPRITE =
{
	{
		// sprite's type
		__TYPE($MAPTYPE$Sprite),

		// texture definition
		(TextureDefinition*)&$NAME$_TX,

		// transparent (__TRANSPARENCY_NONE, __TRANSPARENCY_EVEN or __TRANSPARENCY_ODD)
		$TRANSPARENCY$,

		// displacement
		{$DISPX$, $DISPY$, $DISPZ$, $DISPP$},
	},

	// bgmap mode (__WORLD_BGMAP, __WORLD_AFFINE, __WORLD_OBJECT or __WORLD_HBIAS)
	// make sure to use the proper corresponding sprite type throughout the definition (BgmapSprite or ObjectSprite)
	__WORLD_$MODE$,

	// display mode (__WORLD_ON, __WORLD_LON or __WORLD_RON)
	__WORLD_$DISPMODE$,
};

$MAPTYPE$SpriteROMDef* const $NAME$_SPRITES[] =
{
	&$NAME$_SPRITE,
	NULL
};


//---------------------------------------------------------------------------------------------------------
// 											ENTITY DEFINITION
//---------------------------------------------------------------------------------------------------------

$CLASS$ROMDef $NAME$_EN =
{
	// class allocator
	__TYPE($CLASS$),

	// sprites
    (SpriteROMDef**)$NAME$_SPRITES,

	// collision shapes
	(ShapeDefinition*)NULL,

	// size
	// if 0, width and height will be inferred from the first sprite's texture's size
	// collision detection gap (up, down, left, right)
	{$WIDTH$, $HEIGHT$, $DEPTH$},

	// gameworld's character's type
	$IGTYPE$,

	// physical specification
	(PhysicalSpecification*)NULL,
};
